import { useContext } from 'react';
import { ThemeContext } from './context.js';
import { MissingAppProviderError } from '../errors.js';

function useTheme() {
  var theme = useContext(ThemeContext);

  if (!theme) {
    throw new MissingAppProviderError('No Theme was provided.');
  }

  return theme;
}

export { useTheme };
